import React, { useState, useEffect } from "react";
import NavigationBar from "../components/NavigationBar";
import { db } from "../firebaseConfig";
import { collection, getDocs } from "firebase/firestore";
import ReactPlayer from "react-player";

function MoviesPage() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    const querySnapshot = await getDocs(collection(db, "movies"));
    setMovies(querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
  };

  return (
    <div className="bg-black min-h-screen text-white">
      <NavigationBar />
      <div className="container mx-auto py-6">
        <h1 className="text-4xl font-bold text-center mb-8">ðŸŽ¬ Featured Films</h1>

        {/* Movie Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {movies.map((movie) => (
            <div key={movie.id} className="group relative overflow-hidden rounded-lg shadow-lg bg-gray-900 hover:scale-105 transition duration-300 ease-in-out">
              {/* Video Player */}
              <ReactPlayer
                url={movie.url}
                className="w-full h-[300px] md:h-[400px] group-hover:brightness-75 transition duration-300"
                controls
                width="100%"
                height="100%"
              />

              {/* Movie Info Overlay */}
              <div className="absolute bottom-0 left-0 right-0 p-4 bg-gradient-to-t from-black to-transparent">
                <h2 className="text-xl md:text-2xl font-semibold">{movie.name}</h2>
                <p className="text-sm text-gray-300 mt-1">
                  <strong>Premiered:</strong> {movie.premiered}
                </p>
                <p className="text-gray-400 text-sm mt-2 line-clamp-3">{movie.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default MoviesPage;
