import React, { useState } from "react";
import NavigationBar from "../components/NavigationBar";

function MusicPage() {
  const [tracks, setTracks] = useState([]);
  const [isAdmin, setIsAdmin] = useState(true); // Change this as needed
  const [step, setStep] = useState(0);
  const [newTrack, setNewTrack] = useState({ name: "", url: "", image: "", album: "", isSingle: false });
  const [albums, setAlbums] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type === "audio/wav") {
      setNewTrack({ ...newTrack, url: URL.createObjectURL(file), name: file.name });
      setStep(1);
    } else {
      alert("Please upload a valid WAV file.");
    }
  };

  const handleNameSelection = (useDefault) => {
    if (useDefault) {
      setStep(2);
    }
  };

  const handleAlbumSelection = (album) => {
    if (album === "new") {
      const newAlbum = prompt("Enter new album name:");
      if (newAlbum) {
        setAlbums([...albums, newAlbum]);
        setNewTrack({ ...newTrack, album: newAlbum });
      }
    } else if (album === "single") {
      setNewTrack({ ...newTrack, isSingle: true });
    } else {
      setNewTrack({ ...newTrack, album });
    }
    setStep(3);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith("image/")) {
      setNewTrack({ ...newTrack, image: URL.createObjectURL(file) });
      setStep(4);
    } else {
      alert("Please upload a valid image file.");
    }
  };

  const finalizeTrack = () => {
    setTracks([...tracks, newTrack]);
    setStep(0);
    setNewTrack({ name: "", url: "", image: "", album: "", isSingle: false });
  };

  return (
    <div className="page-container p-4">
      <NavigationBar />
      <h1 className="text-2xl font-bold">Music</h1>
      <p>Add music content here, such as audio tracks or interactive features.</p>
      
      {isAdmin && step === 0 && (
        <div className="mb-4">
          <p>Upload a WAV file:</p>
          <input type="file" accept="audio/wav" onChange={handleFileUpload} />
        </div>
      )}

      {step === 1 && (
        <div className="mb-4">
          <p>Enter track name:</p>
          <input type="text" value={newTrack.name} onChange={(e) => setNewTrack({ ...newTrack, name: e.target.value })} />
          <label>
            <input type="checkbox" checked={newTrack.name === "file name which is already there"} onChange={() => handleNameSelection(true)} /> Use file name
          </label>
          <button onClick={() => setStep(2)}>Next</button>
        </div>
      )}

      {step === 2 && (
        <div className="mb-4">
          <p>Does this track belong to an album?</p>
          {albums.map((album) => (
            <button key={album} onClick={() => handleAlbumSelection(album)}>{album}</button>
          ))}
          <button onClick={() => handleAlbumSelection("new")}>Create New Album</button>
          <button onClick={() => handleAlbumSelection("single")}>Upload as Single</button>
        </div>
      )}

      {step === 3 && (
        <div className="mb-4">
          <p>Upload a display picture:</p>
          <input type="file" accept="image/*" onChange={handleImageUpload} />
        </div>
      )}

      {step === 4 && (
        <div className="mb-4">
          <p>Finalize upload:</p>
          <button onClick={finalizeTrack}>Post Song</button>
        </div>
      )}
      
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {tracks.map((track, index) => (
          <div key={index} className="p-4 border rounded-lg shadow-lg">
            <img src={track.image} alt={track.name} className="w-full h-40 object-cover mb-2" />
            <h2 className="text-lg font-semibold">{track.name}</h2>
            <p>{track.album ? `Album: ${track.album}` : "Single"}</p>
            <audio controls className="w-full">
              <source src={track.url} type="audio/wav" />
              Your browser does not support the audio element.
            </audio>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MusicPage;
