import React, { useState, useEffect } from "react";
import NavigationBar from "../components/NavigationBar";
import ReactPlayer from "react-player";
import { db } from "../firebaseConfig";
import { collection, getDocs, addDoc, updateDoc, deleteDoc, doc } from "firebase/firestore";

function MoviesPage() {
  const [movies, setMovies] = useState([]);
  const [newMovie, setNewMovie] = useState({ name: "", url: "", premiered: "", description: "", credits: "" });
  const [editingMovie, setEditingMovie] = useState(null);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    const querySnapshot = await getDocs(collection(db, "movies"));
    setMovies(querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
  };

  const handleAddMovie = async () => {
    if (!newMovie.name || !newMovie.url) {
      alert("Movie Name and URL are required");
      return;
    }

    await addDoc(collection(db, "movies"), newMovie);
    setNewMovie({ name: "", url: "", premiered: "", description: "", credits: "" });
    fetchMovies();
  };

  const handleUpdateMovie = async (id) => {
    const movieRef = doc(db, "movies", id);
    await updateDoc(movieRef, editingMovie);
    setEditingMovie(null);
    fetchMovies();
  };

  const handleDeleteMovie = async (id) => {
    await deleteDoc(doc(db, "movies", id));
    fetchMovies();
  };

  return (
    <div className="page-container">
      <NavigationBar />
      <h1 className="text-2xl font-bold">Movies</h1>

      {/* Add Movie Form */}
      <div className="mb-4 p-4 border rounded-lg shadow-lg">
        <h2 className="text-xl font-semibold">Add New Movie</h2>
        <input type="text" placeholder="Name" value={newMovie.name} onChange={(e) => setNewMovie({ ...newMovie, name: e.target.value })} />
        <input type="text" placeholder="YouTube URL" value={newMovie.url} onChange={(e) => setNewMovie({ ...newMovie, url: e.target.value })} />
        <input type="text" placeholder="Premiered Date" value={newMovie.premiered} onChange={(e) => setNewMovie({ ...newMovie, premiered: e.target.value })} />
        <input type="text" placeholder="Description" value={newMovie.description} onChange={(e) => setNewMovie({ ...newMovie, description: e.target.value })} />
        <input type="text" placeholder="Credits" value={newMovie.credits} onChange={(e) => setNewMovie({ ...newMovie, credits: e.target.value })} />
        <button onClick={handleAddMovie}>Add Movie</button>
      </div>

      {/* Movie List */}
      <div className="movie-list">
        {movies.map((movie) => (
          <div key={movie.id} className="movie-item p-4 border rounded-lg shadow-lg">
            <ReactPlayer url={movie.url} controls width="100%" height="180px" />
            <h3 className="text-lg font-semibold">{movie.name}</h3>
            <p><strong>Premiered:</strong> {movie.premiered}</p>
            <p><strong>Description:</strong> {movie.description}</p>
            <p><strong>Credits:</strong> {movie.credits}</p>

            {/* Edit Movie */}
            {editingMovie?.id === movie.id ? (
              <>
                <input type="text" value={editingMovie.name} onChange={(e) => setEditingMovie({ ...editingMovie, name: e.target.value })} />
                <button onClick={() => handleUpdateMovie(movie.id)}>Save</button>
              </>
            ) : (
              <button onClick={() => setEditingMovie(movie)}>Edit</button>
            )}

            {/* Delete Movie */}
            <button onClick={() => handleDeleteMovie(movie.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MoviesPage;
