import React, { useState } from "react";
import NavigationBar from "../components/NavigationBar";

function GalleryPage() {
  // State to store sessions (each with a name and pictures array)
  const [sessions, setSessions] = useState([]);
  // State to track the currently selected session (by its index)
  const [selectedSessionIndex, setSelectedSessionIndex] = useState(null);
  // State to manage the new session name input
  const [newSessionName, setNewSessionName] = useState("");

  // Create a new session (folder)
  const handleCreateSession = () => {
    if (newSessionName.trim() === "") {
      alert("Session name cannot be empty");
      return;
    }
    const newSession = { name: newSessionName, pictures: [] };
    setSessions([...sessions, newSession]);
    setNewSessionName("");
  };

  // Upload a picture into the selected session
  const handlePictureUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith("image/")) {
      const imageUrl = URL.createObjectURL(file);
      // Create a picture object with the file name and URL
      const newPicture = { url: imageUrl, name: file.name };
      // Copy sessions and update the selected session's pictures array
      const updatedSessions = [...sessions];
      updatedSessions[selectedSessionIndex].pictures.push(newPicture);
      setSessions(updatedSessions);
    } else {
      alert("Please upload a valid image file.");
    }
  };

  return (
    <div className="page-container p-4">
      <NavigationBar />
      <h1 className="text-2xl font-bold">Gallery</h1>
      <p>Upload pictures by creating sessions (folders) first.</p>

      {/* Section for admin to create a new session */}
      <div className="mb-4 border p-4 rounded-lg shadow-lg">
        <h2 className="text-xl font-semibold">Create a New Session</h2>
        <input
          type="text"
          placeholder="Session name"
          value={newSessionName}
          onChange={(e) => setNewSessionName(e.target.value)}
          className="border p-2 mr-2"
        />
        <button
          onClick={handleCreateSession}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Create Session
        </button>
      </div>

      {/* List of sessions */}
      <div className="mb-4">
        <h2 className="text-xl font-semibold">Sessions</h2>
        {sessions.length === 0 ? (
          <p>No sessions available. Create one to start uploading images.</p>
        ) : (
          <ul>
            {sessions.map((session, index) => (
              <li
                key={index}
                className="mb-2 cursor-pointer text-blue-600"
                onClick={() => setSelectedSessionIndex(index)}
              >
                {session.name}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Display pictures for the selected session */}
      {selectedSessionIndex !== null && (
        <div className="mb-4 border p-4 rounded-lg shadow-lg">
          <h2 className="text-xl font-semibold">
            Session: {sessions[selectedSessionIndex].name}
          </h2>
          <p>Upload a picture to this session:</p>
          <input type="file" accept="image/*" onChange={handlePictureUpload} className="mb-2" />
          {sessions[selectedSessionIndex].pictures.length === 0 ? (
            <p>No pictures in this session.</p>
          ) : (
            <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-4 mt-4">
              {sessions[selectedSessionIndex].pictures.map((pic, idx) => (
                <div key={idx} className="border p-2 rounded">
                  <img
                    src={pic.url}
                    alt={pic.name}
                    className="w-full h-32 object-cover mb-2"
                  />
                  <p className="text-center text-sm">{pic.name}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default GalleryPage;
