import React, { useState, useEffect } from "react";
import NavigationBar from "../components/NavigationBar";
import { db } from "../firebaseConfig";
import { collection, getDocs, addDoc } from "firebase/firestore";
import "../styles/movies.css";

function MoviesPage() {
  const [movies, setMovies] = useState([]);
  const [newMovie, setNewMovie] = useState({ name: "", url: "", premiered: "", description: "" });
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    const querySnapshot = await getDocs(collection(db, "movies"));
    setMovies(querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
  };

  const extractVideoId = (url) => {
    try {
      let videoId = "";
      const parsedUrl = new URL(url);

      if (parsedUrl.hostname.includes("youtube.com")) {
        videoId = parsedUrl.searchParams.get("v");
      } else if (parsedUrl.hostname.includes("youtu.be")) {
        videoId = parsedUrl.pathname.substring(1);
      }

      return videoId;
    } catch (error) {
      console.error("Error extracting Video ID:", error);
      return null;
    }
  };

  const handleAddMovie = async () => {
    if (!newMovie.name || !newMovie.url) {
      alert("Movie Name and URL are required");
      return;
    }

    await addDoc(collection(db, "movies"), newMovie);
    setNewMovie({ name: "", url: "", premiered: "", description: "" });
    fetchMovies();
  };

  return (
    <div className="movies-container">
      <NavigationBar />
      <h1 className="movies-title">ðŸŽ¬ Featured Movies</h1>

      <div className="add-movie-form">
        <input
          type="text"
          placeholder="YouTube URL"
          value={newMovie.url}
          onChange={(e) => setNewMovie({ ...newMovie, url: e.target.value })}
        />
        <input
          type="text"
          placeholder="Name"
          value={newMovie.name}
          onChange={(e) => setNewMovie({ ...newMovie, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Premiered Date"
          value={newMovie.premiered}
          onChange={(e) => setNewMovie({ ...newMovie, premiered: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newMovie.description}
          onChange={(e) => setNewMovie({ ...newMovie, description: e.target.value })}
        />
        <button onClick={handleAddMovie}>Add Movie</button>
      </div>

      <div className="movies-grid">
        {movies.map((movie) => (
          <div key={movie.id} className="movie-card" onClick={() => setSelectedMovie(movie)}>
            <img
              src={`https://img.youtube.com/vi/${extractVideoId(movie.url)}/maxresdefault.jpg`}
              alt={movie.name}
            />
            <div className="movie-card-content">
              <h3 className="movie-title">{movie.name}</h3>
              <p className="movie-date">
                <span>Premiered:</span> {movie.premiered}
              </p>
              <p className="movie-description">{movie.description}</p>
            </div>
          </div>
        ))}
      </div>

      {selectedMovie && (
        <>
          <div className="modal-overlay" onClick={() => setSelectedMovie(null)}></div>
          <div className="video-modal">
            <iframe
              width="800"
              height="450"
              src={`https://www.youtube.com/embed/${extractVideoId(selectedMovie.url)}?autoplay=1`}
              title={selectedMovie.name}
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        </>
      )}
    </div>
  );
}

export default MoviesPage;
