import React, { useState, useEffect } from "react";
import NavigationBar from "../components/NavigationBar";
import { uploadGalleryImage, fetchGallery } from "../services/firestoreService";

function GalleryPage() {
  const [images, setImages] = useState([]);
  const [albumName, setAlbumName] = useState("");
  const [file, setFile] = useState(null);

  useEffect(() => {
    fetchGallery().then(setImages);
  }, []);

  const handleUpload = async () => {
    if (!file || !albumName) {
      alert("Please enter an album name and select an image.");
      return;
    }

    const result = await uploadGalleryImage(file, albumName);
    if (result.success) {
      alert("Image uploaded successfully!");
      setImages([...images, { album: albumName, imageUrl: result.url }]);
      setAlbumName("");
      setFile(null);
    } else {
      alert("Upload failed. Check the console for details.");
    }
  };

  return (
    <div className="page-container p-4">
      <NavigationBar />
      <h1 className="text-2xl font-bold">Gallery</h1>
      
      <input type="text" placeholder="Album Name" value={albumName} onChange={(e) => setAlbumName(e.target.value)} />
      <input type="file" accept="image/*" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={handleUpload}>Upload Image</button>

      <div className="grid grid-cols-2 sm:grid-cols-3 gap-4">
        {images.map((img, index) => (
          <img key={index} src={img.imageUrl} alt="Gallery" className="w-full h-40 object-cover" />
        ))}
      </div>
    </div>
  );
}

export default GalleryPage;
