import React, { useState, useEffect } from "react";
import NavigationBar from "../components/NavigationBar";
import ReactPlayer from "react-player";
import { db } from "../firebaseConfig";
import { collection, getDocs } from "firebase/firestore";

function MoviesPage() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "movies"));
        const movieList = querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
        setMovies(movieList);
      } catch (error) {
        console.error("Error fetching movies:", error);
      }
    };

    fetchMovies();
  }, []);

  return (
    <div className="page-container">
      <NavigationBar />
      <h1 className="text-2xl font-bold">Movies</h1>
      <div className="movie-list">
        {movies.length === 0 ? (
          <p>No movies available. Add movies in Firestore.</p>
        ) : (
          movies.map((movie, index) => (
            <div key={index} className="movie-item p-4 border rounded-lg shadow-lg">
              <ReactPlayer url={movie.url} controls={true} width="100%" height="180px" />
              <div className="movie-details">
                <h3 className="text-lg font-semibold">{movie.name}</h3>
                <p><strong>Premiered:</strong> {movie.premiered}</p>
                <p><strong>Description:</strong> {movie.description}</p>
                <p><strong>Credits:</strong> {movie.credits}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default MoviesPage;
