import React, { useState, useEffect } from "react";
import NavigationBar from "../components/NavigationBar";
import { uploadMusic, fetchMusic } from "../services/firestoreService";

function MusicPage() {
  const [tracks, setTracks] = useState([]);
  const [trackName, setTrackName] = useState("");
  const [albumName, setAlbumName] = useState("");
  const [file, setFile] = useState(null);
  const [coverImage, setCoverImage] = useState(null);

  useEffect(() => {
    fetchMusic().then(setTracks);
  }, []);

  const handleUpload = async () => {
    if (!file || !trackName) {
      alert("Please select a file and enter a track name.");
      return;
    }

    const result = await uploadMusic(file, trackName, albumName, coverImage);
    if (result.success) {
      alert("Music uploaded successfully!");
      setTracks([...tracks, { name: trackName, album: albumName, url: result.url, coverImage: result.coverImage }]);
      setTrackName("");
      setAlbumName("");
      setFile(null);
      setCoverImage(null);
    } else {
      alert("Upload failed. Check the console for details.");
    }
  };

  return (
    <div className="page-container p-4">
      <NavigationBar />
      <h1 className="text-2xl font-bold">Music</h1>
      
      <div className="upload-form">
        <input type="text" placeholder="Track Name" value={trackName} onChange={(e) => setTrackName(e.target.value)} />
        <input type="text" placeholder="Album Name (optional)" value={albumName} onChange={(e) => setAlbumName(e.target.value)} />
        <input type="file" accept="audio/wav" onChange={(e) => setFile(e.target.files[0])} />
        <input type="file" accept="image/*" onChange={(e) => setCoverImage(e.target.files[0])} />
        <button onClick={handleUpload}>Upload Music</button>
      </div>

      <div className="track-list grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {tracks.map((track, index) => (
          <div key={index} className="p-4 border rounded-lg shadow-lg">
            {track.coverImage && <img src={track.coverImage} alt={track.name} className="w-full h-40 object-cover mb-2" />}
            <h2 className="text-lg font-semibold">{track.name}</h2>
            <p>{track.album ? `Album: ${track.album}` : "Single"}</p>
            <audio controls className="w-full">
              <source src={track.url} type="audio/wav" />
              Your browser does not support the audio element.
            </audio>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MusicPage;
